CREATE TABLE eng AS select * from text where lang = 'en';

select * from eng where text LIKE '%santa%' OR text LIKE '%SANTA%' OR text LIKE '%claus%' OR text LIKE '%CLAUS%' OR text LIKE '%merry%' OR text LIKE '%MERRY%' OR text LIKE '%christmas%' OR text LIKE '%CHRISTMAS%' OR text LIKE '%eve%' OR text LIKE '%EVE%' OR text LIKE '%congrat%' OR text LIKE '%CONGRAT%' OR text LIKE '%happy%' OR text LIKE '%HAPPY%' OR text LIKE '%holiday%' OR text LIKE '%HOLIDAY%' OR text LIKE '%jingle%' OR text LIKE '%JINGLE%' OR text LIKE '%bell%' OR text LIKE '%BELL%' OR text LIKE '%silent%' OR text LIKE '%SILENT%' OR text LIKE '%night%' OR text LIKE '%NIGHT%' OR text LIKE '%faith%' OR text LIKE '%FAITH%' OR text LIKE '%hope%' OR text LIKE '%HOPE%' OR text LIKE '%family%' OR text LIKE '%FAMILY%' OR text LIKE '%new%' OR text LIKE '%NEW%' OR text LIKE '%year%' OR text LIKE '%YEAR%' OR text LIKE '%spirit%' OR text LIKE '%SPIRIT%' OR text LIKE '%turkey%' OR text LIKE '%TURKEY%' OR text LIKE '%ham%' OR text LIKE '%HAM%' OR text LIKE '%food%' OR text LIKE '%FOOD%';